sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var curfewLocation = new GlideRecord('x_ster2_emfs_curfew_location');

    curfewLocation.addQuery('x_ster2_emfs_address_line_1', current.address_line_1);
    curfewLocation.addQuery('zip', current.post_code);
    curfewLocation.query();
    if (curfewLocation.hasNext() === false) { //Check if record already exists
        curfewLocation.name = current.address_line_1 + ', ' + current.city + ', ' + current.county + ', ' + current.post_code;
        curfewLocation.street = current.address_line_1 + ', ' + current.address_line_2;
        curfewLocation.x_ster2_emfs_address_line_1 = current.address_line_1;
        curfewLocation.x_ster2_emfs_address_line_2 = current.address_line_2;
        curfewLocation.city = current.city;
        curfewLocation.state = current.county;
        curfewLocation.zip = current.post_code;
        curfewLocation.parent.setDisplayValue(current.post_code.split(' ')[0]);
        curfewLocation.insert();
    }


})(current, previous);
