sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    var gr = new GlideRecord('cmn_location');
    var curfewLocation = new GlideRecord('x_ster2_emfs_curfew_location');

    gr.addQuery('x_ster2_emfs_address_line_1', current.address_line_1);
    gr.addQuery('zip', current.post_code);
    gr.query();

    curfewLocation.addQuery('x_ster2_emfs_address_line_1', current.address_line_1);
    curfewLocation.addQuery('zip', current.post_code);
    curfewLocation.query();

    if (curfewLocation.hasNext() === false) { //Check if record already exists
        curfewLocation.name = current.address_line_1 + ', ' + current.city + ', ' + current.county + ', ' + current.post_code;
        curfewLocation.street = current.address_line_1 + ', ' + current.address_line_2;
        curfewLocation.x_ster2_emfs_address_line_1 = current.address_line_1;
        curfewLocation.x_ster2_emfs_address_line_2 = current.address_line_2;
        curfewLocation.city = current.city;
        curfewLocation.state = current.county;
        curfewLocation.zip = current.post_code;
        curfewLocation.insert();
    }

    if (gr.hasNext() === false) { //Check if record already exists
        gr.name = current.address_line_1 + ', ' + current.city + ', ' + current.county + ', ' + current.post_code;
        gr.street = current.address_line_1 + ', ' + current.address_line_2;
        gr.x_ster2_emfs_address_line_1 = current.address_line_1;
        gr.x_ster2_emfs_address_line_2 = current.address_line_2;
        gr.city = current.city;
        gr.state = current.county;
        gr.zip = current.post_code;
//         gr.insert();
        gs.addInfoMessage('Successfully added subject and address to Location and Curfew Location tables!');
    } else { // More records exist
        gs.addInfoMessage('Location already exists in Location and Curfew Address table.');
    }

})(current, previous);
