sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {any} params.imports
  */
function evaluateProperty({ api }) {
  let output = api.data.get_auto_assignment_results.output;
  if (output && output.resultJSON && output.resultJSON['not_assigned']['section_label'])
    return output.resultJSON['not_assigned']['section_label'];

}
