sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    let tabRoutes = api.data.gform.nowRecordFormBlob.tabRoutes;
    //Retrieve Alerts from tabRoutes array
    const result = tabRoutes.filter(tabRoute => tabRoute.label == "Alerts");
    //Identify index position of Alerts
    const foundIndex = tabRoutes.findIndex(el => el == result[0]);

    if (foundIndex != -1) {
        //Remove Alerts from array and add to beginning position
        tabRoutes.unshift(tabRoutes.splice(foundIndex, 1)[0]);
    }

    return api.data.gform.nowRecordFormBlob.tabRoutes;
}
