sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    switch (api.data.subject.result.risk.value) {
        case 'low':
            var riskString = 'low';
            break;
        case 'medium':
            var riskString = 'medium';
            break;
        case 'high':
            var riskString = 'high';
            break;
        case 'very_high':
            var riskString = 'very high';
            break;
    }
    return 'This subject is ' + riskString + ' risk';
}
