sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    var alertCount = api.data.alerts_count.output.data.GlideAggregate_Query[0].count;

    if (alertCount > 1 || alertCount == 0) {
        var alertText = ' alerts';
    } else {
        var alertText = ' alert';
    }
    return 'This subject has ' + alertCount + ' active' + alertText + '. Click to view.';
}
