sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {TransformApiHelpers} params.helpers
 */
function evaluateProperty({
    api,
    helpers
}) {
    switch (api.data.subject.result.risk.value) {
        case 'low':
            return 'Low';
            break;
        case 'medium':
            return 'Medium';
            break;
        case 'high':
            return 'High';
            break;
        case 'very_high':
            return 'Very High';
            break;
    }
}
